$_MODULE['<{shopylinkerp}prestashop>catalogm_69ddbe5a2d537b9905eec47a33656611'] = 'Shopylinker vous offre deux options d\'affichage.';
$_MODULE['<{shopylinkerp}prestashop>catalogm_c8dc1580349c6233311a257330cd5cc3'] = 'En mode Mosaïque, les objets sont présentés sous forme de cartes montrant les propriétés les plus pertinentes de l\'objet et disposent de boutons interactifs pour un accès rapide à l\'édition de propriétés spécifiques. Si vous préférez travailler de manière plus traditionnelle, vous avez également la possibilité d\'utiliser le mode Liste, qui est similaire à la façon dont il est affiché dans le backoffice PrestaShop mais en ajoutant les mêmes options d\'accessibilité que le mode Mosaïque de Shopylinker.';
$_MODULE['<{shopylinkerp}prestashop>catalogm_5c163ae923dc7897bab6eb0c49e28d31'] = 'Affichage en mode mosaïque';
$_MODULE['<{shopylinkerp}prestashop>catalogm_a4d6c4603eacbb92d19e68c7ca59df69'] = 'Affichage en mode liste';
