$_MODULE['<{shopylinkerp}prestashop>frequentquestion_1431dee96c1ad25b072b8f09cd385ebe'] = ' Plan Essentiel : Fournit le système de gestion et d\'optimisation du catalogue, y compris l\'importation/exportation';
